    function (metaName){
      var metaC;
      try {
          var metas = document.getElementsByTagName('meta');
          for (var i=0; i<metas.length; i++) {
            if (metas[i].getAttribute("name")==metaName ) {
              metaC = metas[i].getAttribute("content");
              console.log(metaC);
            }
          }
      } catch (e) {}
      return metaC;
    }


    function (metaName){
      try {
          var metas = document.getElementsByTagName('meta');
          for (var i=0; i<metas.length; i++) {
            if (metas[i].getAttribute("name")==metaName ) {
              console.log("metaContentExists:true");
              return true;
            }
          }
      } catch (e) {}
      console.log("metaContentExists:false");
      return false;
    }


