  function(automaton, location, delay, data) {
    console.log('=========Nina Triggered Automaton=========');
    console.log(automaton);
    console.log(location);
    console.log(delay);
    console.log(data);
    console.log('========/Nina Triggered Automaton=========');
    try {
      var delay = delay ? delay : 0;
      var data = data ? data : {};
      if(delay != 'kill') {
        window.nuanceVALoginTimer = setTimeout(
          function() {
            inQ.ACIF.loadAutomaton({automatonID: automaton, context: 'ci', location: location, datapass: data},{type: 'agent'});
          }, delay);
      } else {
        clearTimeout(window.nuanceVALoginTimer);
      }
    } catch (e) {};
  }

